이니시스 PG 모듈 스크립트
별도의 백앤드 개발 없이 PG를 커스텀하여 실행 할 수 있음.
모바일과 웹은 @nuxtjs/device 로 분기처리함.
INIpayTest mid는 테스트용!


<code>
let device;
let isInisisLoaded = false;

if (process.browser) {
  window.onNuxtReady(({ $store, $device, $apis }) => {
    device = $device;
  });
}

const inisisPay = async (formData) => {
  const returnUrl = `${window.location.origin}/pg/complete`; // 완료 redirect url
  if (device.isDesktop) { // PC
    if (!isInisisLoaded) {
      (function () {
        const m = document.getElementsByTagName('script')[0];
        const a = document.createElement('script');
        a.async = 1;
        if (formData.mid === 'INIpayTest') {
          a.src = 'https://stgstdpay.inicis.com/stdjs/INIStdPay.js';
        } else {
          a.src = 'https://stdpay.inicis.com/stdjs/INIStdPay.js';
        }
        m.parentNode.insertBefore(a, m);
      })();
      isInisisLoaded = true;
    }
    const time = new Date().getTime();
    try {
      let mKey, signature;
      await apis.sendApi('/sha256/mid', {}, {
        message: formData.mid,
      }).then((resp) => {
        mKey = resp.data;
      });
      await apis.sendApi('/sha256/signature', {}, {
        message: `oid=${formData.oid}&price=${formData.price}&timestamp=${time}`,
      }).then((resp) => {
        signature = resp.data;
      });
      if (document.getElementById('SendPayForm_id')) {
        document.getElementById('SendPayForm_id').remove();
      }
      const formElement = document.createElement('form');
      formElement.id = 'SendPayForm_id';
      formElement.method = 'post';
      const obj = {
        ...formData,
        mKey,
        signature,
        gopaymethod: 'Card',
        timestamp: time,
        version: '1.0',
        currency: 'WON',
        acceptmethod: 'below1000',
        returnUrl: returnUrl,
        closeUrl: `${window.location.origin}/pg/close`
      };
      Object.keys(obj).forEach((key) => {
        const inputElement = document.createElement('input');
        inputElement.setAttribute('type', 'hidden');
        inputElement.setAttribute('name', key);
        inputElement.setAttribute('value', obj[key]);
        formElement.appendChild(inputElement);
      });
      document.body.appendChild(formElement);
      window.INIStdPay.pay('SendPayForm_id');
    } catch (e) {
      alert(e);
    }
  } else { // Mobile
    const formElement = document.createElement('form');
    formElement.name = 'mobileweb';
    formElement.method = 'post';
    formElement.setAttribute('accept-charset', 'euc-kr');
    formElement.action = 'https://mobile.inicis.com/smart/payment/';
    formElement.target = '_self';
    const obj = {
      P_NEXT_URL: returnUrl,
      P_INI_PAYMENT: 'CARD',
      P_RESERVED: 'twotrs_isp=Y&block_isp=Y&twotrs_isp_noti=N&cp_yn=Y',
      P_MID: formData.mid,
      P_OID: formData.oid,
      P_GOODS: formData.goodname,
      P_AMT: formData.price,
      P_UNAME: formData.buyerName,
      P_NOTI_URL: '',
      P_HPP_METHOD: 1,
      P_NOTI: formData.oid,
    };
    Object.keys(obj).forEach((key) => {
      const inputElement = document.createElement('input');
      inputElement.setAttribute('type', 'hidden');
      inputElement.setAttribute('name', key);
      inputElement.setAttribute('value', obj[key]);
      formElement.appendChild(inputElement);
    });
    document.body.appendChild(formElement);
    formElement.submit();
  }
};
</code>

